//上拉加载
- (void)footerAction: (MJRefreshAutoNormalFooter *)aFooter
{
    NSMutableArray *dataNewArray2 =  [NSMutableArray array];

    aFooter.state = MJRefreshStateIdle;
    for (NSString *str in dataArray) {
        [dataNewArray2 addObject:str];
    }
    for (int i = 0; i < 10; i++) {
        int number =  (int)dataNewArray2.count;
        int a = arc4random()%(number - 10  - 1 + 1);
        [dataNewArray2 replaceObjectAtIndex: (number - i - 1) withObject:dataNewArray2[a]];
        
    }
    
    
    dataArray = [NSArray arrayWithArray:dataNewArray2];
    
    
    [self.tableView reloadData];
    

}

//下拉刷新
- (void)headerAction: (MJRefreshNormalHeader *)aNormalHeader
{
    NSMutableArray *dataNewArray =  [NSMutableArray array];
    
    aNormalHeader.state = MJRefreshStateIdle;
    for (NSString *str in dataArray) {
        [dataNewArray addObject:str];
    }
    for (int i = 0; i < 10; i++) {
        int number =  (int)dataNewArray.count;
        int a = arc4random()%(number - 10 + 1);
        [dataNewArray replaceObjectAtIndex:i withObject:dataNewArray[a]];
        
    }
    
    
    dataArray = [NSArray arrayWithArray:dataNewArray];
    
    
    [self.tableView reloadData];
    
    
}

